document.addEventListener("DOMContentLoaded",function(e){for(var t=document.querySelectorAll(".hofff-recursive-download-folder-toggleable .folder.download-element > a"),n=0;n<t.length;n++)t[n].classList.contains("download-as-zip")||t[n].addEventListener("click",function(e){e.preventDefault(),e.target.parentElement.classList.toggle("folder-open")})});
